sys_variable_value.value:
(function execute(inputs, outputs) {
gs.include("ethjs-format");
gs.include("object-assign");
gs.include("ethjs-signer");

gs.info("from: " + tx.from);
gs.info("to: " + tx.to);

var pretx = { "from": tx.from.toString(),
  "to": tx.to.toString(),
  "gas": tx.gas.toString(),
  "gasPrice": tx.gasprice.toString(),
  "value": tx.value.toString(),
  "nonce": tx.nonce.toString(),
  "data": tx.data.toString()
};

gs.info("pretx: " + JSON.stringify(pretx));

var rawtx = ethFormat.formatInputs('eth_sendTransaction', [pretx])[0];

gs.info("tx: " + JSON.stringify(tx));

tx.raw = ethSigner.sign(tx, "0x2f5205aa34bf4c5bdc6c6af8b8384641e4bb1468a64bfdb27cbfa884d14f9296", false);

})(inputs, outputs);

