sys_variable_value.value:
(function execute(inputs, outputs) { 

gs.include("ethjs-format");
gs.include("object-assign");
gs.include("ethjs-signer");

var pretx = { "from": inputs.tx.from.toString(),
  "to": inputs.tx.to.toString(),
  "gas": inputs.tx.gas.toString(),
  "gasPrice": inputs.tx.gasprice.toString(),
  "value": inputs.tx.value.toString(),
  "nonce": inputs.tx.nonce.toString(),
  "data": inputs.tx.data.toString()
};

gs.info("pretx: " + JSON.stringify(pretx));

var fmttx = ethFormat.formatInputs('eth_sendTransaction', [pretx])[0];

gs.info("fmttx: " + JSON.stringify(fmttx));

var params = {};

params.from = fmttx.from.toString();
params.to = fmttx.to.toString();
params.gas = fmttx.gas.toString();
params.gasprice = fmttx.gasPrice.toString();
params.value = fmttx.value.toString();;
params.nonce = fmttx.nonce.toString();;
params.data = fmttx.data.toString();

gs.info("params: " + JSON.stringify(params));

inputs.tx.raw = sn_fd.FlowAPI.executeSubflow('x_snc_ethsign.sign_transaction', params).txraw;
inputs.tx.update();

})(inputs, outputs);

