sys_variable_value.value:
(function execute(inputs, outputs) {

 function sleep(ms) {
       var unixtime_ms = new Date().getTime();
       while(new Date().getTime() < unixtime_ms + ms) {}
}

 for (i=0; i < 10; i++) {

 gs.info("CYCLE " + i + " WAITING FOR " + inputs.txhash);

 sleep(3000);

 var r = new sn_ws.RESTMessageV2();
 r.setEndpoint(inputs.endpoint.uri);
 r.setHttpMethod('post');
 var payload = '{"jsonrpc":"2.0","method":"eth_getTransactionReceipt","params":["' + inputs.txhash + '"],"id":1}';
 r.setRequestBody(payload);
 r.setRequestHeader("Content-Type","application/json");

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();

 receipt = JSON.parse(responseBody).result;

 if (receipt != null ) {break;}

 }

 outputs.receipt = JSON.stringify(receipt);

})(inputs, outputs);

