sys_variable_value.value:
(function execute(inputs, outputs) {
  abi = JSON.parse(inputs.abistring);

  for (i=0; i < abi.length; i++) {
    if (abi[i].type == "function") {

      var gr = new GlideRecord('x_snc_ethereum_method');
      gr.initialize();
      gr.name = abi[i].name;  
      gr.smart_contract_instance = inputs.scisysid;
      gr.number =i;
      gr.insert();

	}	  
  }
})(inputs, outputs);

