sys_variable_value.value:
(function execute(inputs, outputs) {
  abi = JSON.parse(inputs.sci.smart_contract.abi);
  var params_list;

  for (i=0; i < abi.length; i++) {
    if (abi[i].type == "function") {

      params_list = "";
      for (j=0; j < abi[i].inputs.length; j++) { params_list += ( abi[i].inputs[j].type + "," ) }
      params_list = "(" + params_list.slice(0, -1) + ")";

      var m = new GlideRecord('x_snc_ethereum_method');
      m.initialize();
      m.name = abi[i].name + params_list + "@" + inputs.sci.name;
      m.fragment = JSON.stringify(abi[i]); 
      m.state_mutability = abi[i].stateMutability;
      m.smart_contract_instance = inputs.sci.sys_id;
      m.number =i;
      m.insert();

	} else if (abi[i].type == "event") {

      var e = new GlideRecord('x_snc_ethereum_event');
      e.initialize();
      e.name = abi[i].name;  
      e.smart_contract_instance = inputs.sci.sys_id;
      e.number =i;
      e.insert();

    }
  }
})(inputs, outputs);

