sys_variable_value.value:
(function execute(inputs, outputs) {
  abi = JSON.parse(inputs.sc.abi);

  for (i=0; i < abi.length; i++) {
    if (abi[i].type == "function") {

      var m = new GlideRecord('x_snc_ethereum_method');
      m.initialize();
      m.name = abi[i].name;
      m.fragment = JSON.stringify(abi[i]); 
      m.state_mutability = abi[i].stateMutability;
      m.smart_contract = inputs.sc.sys_id;
      m.number =i;
      m.insert();

	} else if (abi[i].type == "constructor") {

      var c = new GlideRecord('x_snc_ethereum_constructor');
      c.initialize();
      c.name = abi[i].name;  
      c.smart_contract = inputs.sc.sys_id;
      c.fragment = JSON.stringify(abi[i]); 
      c.number =i;
      c.insert();

    } else if (abi[i].type == "event") {

      var e = new GlideRecord('x_snc_ethereum_event');
      e.initialize();
      e.name = abi[i].name;  
      e.smart_contract = inputs.sc.sys_id;
      e.number =i;
      e.insert();

    }
  }
})(inputs, outputs);

