sys_variable_value.value:
(function execute(inputs, outputs) {

  gs.include("ethjs-abi");

  var abi = JSON.parse(inputs.sc.abi);

  var constructor_index;
  var constructor_found = 0;

  for (i = 0; i < abi.length; i++) {
    if (abi[i].type == "constructor") {
      constructor_index = i;
      constructor_found++;
    }
  }

  if (constructor_found == 1) { outputs.encoded_constructor = ethAbi.encodeMethod(abi[constructor_index], JSON.parse(inputs.params)).substring(10) }

})(inputs, outputs);

