sys_variable_value.value:
(function execute(inputs, outputs) {

var payload2 = JSON.parse(inputs.payload2)

var estimated_gas = JSON.parse(inputs.response_body_gas).result;
var estimated_gas_price = JSON.parse(inputs.response_body_gas_price).result;

gs.info("estimated_gas: " + estimated_gas);
gs.info("estimated_gas_price" + estimated_gas_price);

payload2.gas = estimated_gas;
payload2.gasPrice = estimated_gas_price;

outputs.payload3 = JSON.stringify(payload2);

})(inputs, outputs);

